webgl.modelLoading:
import { useGLTF } from "@react-three/drei";

const MODEL_URL = "https://cdn.builder.io/path-to-your-model.gltf";

function ImacModel() {
  const { scene } = useGLTF(MODEL_URL);
  return <primitive object={scene} scale={1} />;
}

useGLTF.preload(MODEL_URL);

webgl.scrollProgress:
function useScrollProgress() {
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const handler = () => {
      const total = document.body.scrollHeight - window.innerHeight;
      setProgress(total > 0 ? Math.min(Math.max(window.scrollY / total, 0), 1) : 0);
    };

    handler();
    window.addEventListener("scroll", handler, { passive: true });
    window.addEventListener("resize", handler);

    return () => {
      window.removeEventListener("scroll", handler);
      window.removeEventListener("resize", handler);
    };
  }, []);

  return progress;
}

gsap.scrollTrigger:
const tl = gsap.timeline({
  scrollTrigger: {
    trigger: containerRef.current,
    start: "top top",
    end: "bottom bottom",
    scrub: 1,
    invalidateOnRefresh: true,
  },
});

css.viewTimeline:
@supports (animation-timeline: view()) {
  @keyframes wipe-out {
    0% { clip-path: inset(0 0 0% 0); }
    100% { clip-path: inset(0 0 100% 0); }
  }

  .video-layer {
    animation: wipe-out 1s linear both;
    animation-range: entry 0% contain 0%;
  }
}

